import React , { useContext } from "react";
import withStyles from "@material-ui/core/styles/withStyles";
import IconButton from "@material-ui/core/IconButton";
import ThumbUpIcon from "@material-ui/icons/ThumbUp";

import { UserContext } from '../../Root';
import { Mutation } from "react-apollo";
import { gql } from "apollo-boost";

const LikeTrack = ({ classes, tracks, likeCount }) => {

  const currentUser = useContext(UserContext)


  const handleDisabledLikedTrack = () => {
    const userLikes = currentUser.likeSet;
    const isTrackLiked = userLikes.findIndex(({tracks}) => track.id ===tracks.id) > -1 ;
    return isTrackLiked;

  }

  return (
    <Mutation
      mutation={CREATE_LIKE_MUTATION}
      variables={{ trackId: tracks.id }}
      onCompleted={data => {
        console.log(data);
      }}
    >
      {createLike => (
        <IconButton
          onClick={event => {
            event.stopPropagation();
            createLike();
          }}
          className={classes.iconButton}
          disabled={(handleDisabledLikedTrack())}
        >
          {likeCount}
          <ThumbUpIcon className={classes.icon} />
        </IconButton>
      )}
    </Mutation>
  );
};

const CREATE_LIKE_MUTATION = gql`
  mutation($trackId: Int!) {
    createLike(trackId: $trackId) {
      track {
        id
        likeSet {
          id
        }
      }
    }
  }
`;

const styles = theme => ({
  iconButton: {
    color: "deeppink"
  },
  icon: {
    marginLeft: theme.spacing.unit / 2
  }
});

export default withStyles(styles)(LikeTrack);
